import { ethers } from 'ethers';

const data = "";

const iface = new ethers.Interface([
  "function createDispute(address _marketAddress, string calldata _reason, string calldata _evidence, bytes32 _evidenceHash) external returns (uint256)"
]);

const decoded = iface.parseTransaction({ data });
console.log('Function:', decoded.name);
console.log('Market Address:', decoded.args[0]);
console.log('Reason:', decoded.args[1]);
console.log('Reason length:', decoded.args[1].length);
console.log('Evidence:', decoded.args[2]);
console.log('Evidence length:', decoded.args[2].length);
console.log('Evidence Hash:', decoded.args[3]);
